mapper.select:
        SELECT


--         级联表的字段

        FROM shoufeixinxi  a



                and a.id in

                #{item}



                and a.mingmu_name like CONCAT('%',#{params.mingmuName},'%')


                and a.shoufeixinxi_types = #{params.shoufeixinxiTypes}


                  and a.shoufeixinxi_yushou_money >= #{params.shoufeixinxiYushouMoneyStart}   


                  and a.shoufeixinxi_yushou_money <= #{params.shoufeixinxiYushouMoneyEnd}   


                  and a.shoufeixinxi_shishou_money >= #{params.shoufeixinxiShishouMoneyStart}   


                  and a.shoufeixinxi_shishou_money <= #{params.shoufeixinxiShishouMoneyEnd}   


                  and UNIX_TIMESTAMP(a.shoufei_time) >= UNIX_TIMESTAMP(#{params.shoufeiTimeStart}) 


                  and UNIX_TIMESTAMP(a.shoufei_time) <= UNIX_TIMESTAMP(#{params.shoufeiTimeEnd}) 


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




        order by a.${params.orderBy} desc
